import React, { createRef, useEffect } from 'react';
import Header from '../components/Header';
import { Panel } from '../components/Panel';
import faker from 'faker';

const ProfileApp = () => {
	const headerRef = createRef<HTMLDivElement>();
	const containerRef = createRef<HTMLDivElement>();

	useEffect(() => {
		containerRef.current!.style.height = `calc(100vh - ${
			headerRef.current!.getBoundingClientRect().height
		}px)`;
	}, []);

	return (
		<div className="h-full">
			<Header ref={headerRef} />
			<div
				ref={containerRef}
				className="px-12.5% py-12 overflow-hidden overflow-y-scroll"
			>
				{/* <Panel
						compact={true}
						noPadding={true}
						className="rounded-md"
						// header={{ transparent: true, content: `RiznSun's Profile` }}
					>
						<img src={faker.image.imageUrl()} className="rounded-t-md"></img>
					</Panel> */}
				<Panel compact={true} noPadding={true}>
					<div
						className="aspect-w-16 aspect-h-3 bg-black bg-opacity-50"
						style={{ filter: 'brightness(60%)' }}
						// style={{ paddingBottom: '60%' }}
					>
						<img
							src={faker.image.imageUrl()}
							className="rounded-t-md w-full object-cover"
						/>
					</div>
					<div className="grid grid-cols-7 px-12 transform -translate-y-1/2">
						<h3>RiznSun</h3>
						<Panel noPadding={true}>
							<div
								className="w-full relative overflow-hidden"
								style={{ paddingBottom: '100%' }}
							>
								<img
									src={faker.image.imageUrl()}
									className="absolute rounded-md h-full w-full object-cover"
								/>
							</div>
							<div className="p-3 flex flex-col items-center"></div>
						</Panel>
					</div>
				</Panel>
			</div>
		</div>
	);
};

export default ProfileApp;
