import { CSSProperties } from 'react';
import { ReactNode, Ref } from 'react';

const Panel = ({
	children,
	compact,
	header,
	className,
	transparent,
	style,
	noPadding,
}: {
	children?: ReactNode;
	compact?: boolean;
	header?: {
		content: ReactNode | string;
		transparent: boolean;
		// noPadding: boolean;
	};
	transparent?: boolean;
	className?: string;
	style?: CSSProperties;
	noPadding?: boolean;
}) => {
	return (
		<div
			style={style || {}}
			className={`${
				!transparent && 'bg-secondary-bg'
			} w-full h-fit rounded-md ${!noPadding && (compact ? 'p-4' : 'p-6')} ${
				className || ''
			}`}
		>
			{header && (
				<div className={`${!header.transparent ? 'bg-tertiary-bg' : ''}}`}>
					{header.content}
				</div>
			)}
			{children}
		</div>
	);
};

export { Panel };
