import { CSSProperties } from 'react';
import { ReactNode, Ref } from 'react';

const Panel = ({
	children,
	compact,
	header,
	className,
	transparent,
	style,
	noPadding,
}: {
	children?: ReactNode;
	compact?: boolean;
	header?: {
		content?: ReactNode | string;
		transparent?: boolean;
		// noPadding: boolean;
	};
	transparent?: boolean;
	className?: string;
	style?: CSSProperties;
	noPadding?: boolean;
}) => {
	return (
		<div
			style={style || {}}
			className={`${
				!transparent && 'bg-secondary-bg'
			} w-full h-fit rounded-md ${className || ''}`}
		>
			{header && (
				<div
					className={`rounded-t-md ${compact ? 'p-4' : 'p-6'} ${
						!header.transparent
							? 'bg-tertiary-bg'
							: 'border-primary-border border-b-2'
					}`}
				>
					{header.content instanceof String ||
					typeof header.content === 'string' ? (
						<h4 className="font-semibold">{header.content}</h4>
					) : (
						header.content
					)}
				</div>
			)}
			{children && // <div className={`${!noPadding && (compact ? 'p-4' : 'p-6')}`}>
			// 	{children}
			// </div>
			{ children }}
		</div>
	);
};

export { Panel };
