import { CSSProperties } from 'react';
import { ReactNode, Ref } from 'react';

const Panel = ({
	children,
	compact,
	header,
	className,
	transparent,
	style,
	noPadding,
}: {
	children?: ReactNode;
	compact?: boolean;
	header?: ReactNode;
	transparent?: boolean;
	className?: string;
	style?: CSSProperties;
	noPadding?: boolean;
}) => {
	return (
		<div
			style={style || {}}
			className={`${
				!transparent && 'bg-secondary-bg'
			} w-full h-fit rounded-md ${!noPadding && (compact ? 'p-3' : 'p-6')} ${
				className || ''
			}`}
		>
			{header && <div className="bg-tertiary-bg p-3">{header}</div>}
			{children}
		</div>
	);
};

export { Panel };
