import { createRef, useEffect, useState } from 'react';
import { AlertCircle, CheckCircle, Search, Star } from 'react-feather';
import Header2 from '../components/Header2';
import TemplateOne from '../components/templates/Template1';
import TemplateTwo from '../components/templates/Template2';
import TemplateThree from '../components/templates/Template3';
import Link from 'next/link';
import faker from 'faker';
import Unsplash from 'react-unsplash-wrapper';
import EditorContext, {
	Product,
	Scene,
	SceneManager,
} from '../react/editor-context';
import DiscordStatus from '../react/discord-status';
import PreviewStatusBar from '../components/preview/PreviewStatusBar';
import ScenePane from '../components/preview/ScenePane';
import {
	isSceneType,
	SceneManagerReducer,
} from '../react/scenemanager-reducer';
import { nanoid } from 'nanoid';
import EditorInputType from '../react/editor-input';
const parse = require('html-react-parser');
const Handlebars = require('handlebars');
// import raw from '../hbs/index.txt';
// var HandlebarsReact = require('handlebars-react');

// const PRODUCT_NAME = 'Free Template #1';

const hbsString = `<style>
.template-two-bg {
	/* @apply bg-test-banner; */
	background: linear-gradient(rgba(54, 57, 63, 0.85), rgba(54, 57, 63, 1)),
		url('{{#if backgroundUrl}}{{backgroundUrl}}{{else}}/images/background.jpeg{{/if}}');
	background-size: cover;
	/* @apply opacity-40; */
}
html {
	color: white;
}
body {
	font-family: 'Inter', sans-serif;
}
h1 {
	font-size: 2.25rem;
	line-height: 2.5rem;
}
h2 {
	font-size: 1.875rem;
	line-height: 2.25rem;
}
h3 {
	font-size: 1.5rem;
	line-height: 2rem;
}
h4 {
	font-size: 1.25rem;
	line-height: 1.75rem;
}
h5 {
	font-size: 1.125rem;
	line-height: 1.75rem;
}
p {
	font-size: 1.125rem;
	line-height: 1.75rem;
	font-family: 'Roboto', sans-serif;
}
.text-xs {
	font-size: 0.875rem;
	line-height: 1.25rem;
}
</style>
<!-- <script
