<div
class="bg-tertiary-bg relative overflow-hidden flex flex-col justify-between"
style="width: 976px; height: 244px"
>
<div
	class="template-two-bg w-full h-full p-6 flex items-center justify-center space-x-6"
>
	<img
		class="rounded-full"
		style="width: 90px; height: 90px; background-color: #43464E;"
		src="{{#if avatarUrl}}{{avatarUrl}}{{else}}/images/avatar.png{{/if}}"
	/>
	<div className="">
		<h3 class="text-white font-semibold flex items-center">
			{{#if username}}{{username}}{{else}}Username{{/if}}<span class="text-gray-300 font-normal">#{{#if discriminator}}{{discriminator}}{{else}}0000{{/if}}</span>
		</h3>
		<div class="flex items-center mt-1">
				{{#if dnd}}
				<div class="w-5 h-5 bg-red-500 rounded-full font-mono flex items-center justify-center">
					<svg
						viewBox="0 0 24 24"
						width="16"
						height="16"
						stroke="black"
						stroke-width="3"
						fill="none"
						stroke-linecap="round"
						stroke-linejoin="round"
						class="css-i6dzq1"
						data-darkreader-inline-fill=""
						data-darkreader-inline-stroke=""
						style="
							--darkreader-inline-fill: none;
							--darkreader-inline-stroke: currentColor;
						"
					>
						<line x1="5" y1="12" x2="19" y2="12"></line>
					</svg>
				</div>
				<p class="ml-2 text-red-500 font-medium">Do Not Disturb</p>
				{{else if idle}}
				<img src="https://emoji.gg/assets/emoji/3929_idle.png" style="width: 16px; height: 16px;" />
				<p class="ml-2 text-yellow-500 font-medium">Idle</p>
				{{else if online}}
				<div class="w-5 h-5 bg-green-500 rounded-full font-mono flex items-center justify-center">
				</div>
				<p class="ml-2 text-green-500 font-medium">Online</p>
				{{else if offline}}
				<div class="w-5 h-5 bg-gray-400 rounded-full font-mono flex items-center justify-center"></div>
				<p class="ml-2 text-gray-400 font-medium">Offline</p>
				{{/if}}
		</div>
	</div>
</div>
