import React from 'react';
// import { createRef, useEffect, useState } from 'react';
import { Search, Star } from 'react-feather';
import Header2 from '../components/Header2';
// import TemplateOne from '../components/templates/Template1';
// import TemplateTwo from '../components/templates/Template2';
// import TemplateThree from '../components/templates/Template3';
import Link from 'next/link';

const StoreApp = () => {
	//FIX OFFSET HEIGHT SCROLLBAR ISSUE

	return (
		<div className="h-full">
			<Header2 />
			{/* <div className="w-full py-3 bg-secondary-bg text-white px-12.5%">
				<p>
					Are you interested in getting access to premium banners, 20% off
					banner packs, & more?{' '}
					<span className="">
						Check out our <span className="underline">premium plans</span>.
					</span>
				</p>
			</div> */}
			<div className="w-full py-12 px-12.5%">
				<h2>Templates Store</h2>
				<h4 className="text-secondary-text font-normal mt-2">
					We have a wide variety of templates both free and paid to choose from.
					Feel free to look around as something might draw your interest.
				</h4>
				<div className="mt-8">
					<div className="bg-secondary-bg px-3 py-2 rounded-sm w-fit flex items-center">
						<Search size={22} />
						<input
							className="bg-transparent ml-3 placeholder-secondary-text"
							placeholder="Search for templates"
						/>
					</div>
				</div>
			</div>
			<div className="px-12.5% pt-6 flex flex-col justify-center bg-primary-bg">
				{/* <div className="w-1/2 mt-12">
					<img src="https://i.imgur.com/vWcjNCX.png" className="rounded-lg" />
				</div> */}
				<div className="flex items-center space-between">
					<p className="font-medium text-white">
						{/* Showing <span>1-100</span> of 239 results */}
					</p>
					<h3>Free Templates</h3>
				</div>
				{/* <hr className="my-3" /> */}
				<div className="grid grid-cols-4 gap-6 mt-4">
					{[
						'Christmas Holiday Pack',
						'Thankgiving Get-together',
						'Easter Pack',
						'Halloween Scares',
					].map((title) => {
						return (
							<Link href="/store/eqeqwe">
								<a>
									<div className="w-full h-full bg-secondary-bg rounded-md overflow-hidden flex flex-col cursor-pointer">
										<div className="relative bg-secondary-bg pb-2/3">
											<img
												className="absolute top-0 w-full h-48 object-cover"
												src="https://images.unsplash.com/photo-1456441240751-2a368527053f?ixlib=rb-1.2.1&q=80&fm=jpg&crop=entropy&cs=tinysrgb&w=1920"
											/>
										</div>
										<div className="p-4 mt-48">
											<h5 className="truncate">{title}</h5>

											<p className="text-base mt-2 line-clamp overflow-hidden">
												Show off your christmas spirit with this large pack
												filled with stockings, candy canes, & more.
											</p>
											{/* <div className="mt-1 flex items-center">
										<Star size={20} fill={'white'} />
										<p className="text-base ml-2">4.1 / 5.0 (24 Reviews)</p>
									</div> */}
											{/* <hr className="my-3" /> */}
											<div className="flex items-center justify-between mt-6">
												<div className="flex items-center space-x-2">
													<div className="w-7 h-7 bg-primary-bg rounded-sm" />
													<p className="text-base whitespace-nowrap">
														StatusCord Official
													</p>
												</div>
												<div className="flex items-center">
													<Star size={22} fill={'white'} strokeWidth={0} />
													<p className="text-base ml-2 whitespace-nowrap">
														4.1
													</p>
												</div>
											</div>
										</div>
									</div>
								</a>
							</Link>
						);
					})}
				</div>
			</div>
		</div>
	);
};

export { StoreApp };
