import { ReactNode, useEffect } from 'react';

const PanelBody = ({
	className,
	children,
}: {
	className?: string;
	children?: ReactNode;
}) => {
	return (
		<div
			className={
				'col-span-5 w-full pt-3 overflow-hidden overflow-y-scroll h-full scrollbar-thin' +
				(className ? ` ${className}` : '')
			}
		>
			{children && children}
		</div>
	);
};

const PanelMain = ({
	ref,
	headerRef,
	className,
	children,
}: {
	ref?: React.RefObject<HTMLDivElement>;
	headerRef?: React.RefObject<HTMLDivElement>;
	className?: string;
	children?: ReactNode;
}) => {
	//FIX OFFSET HEIGHT SCROLLBAR ISSUE
	useEffect(() => {
		if (!ref || !headerRef) {
			return;
		}
		ref.current!.style.height = `calc(100vh - ${
			headerRef.current!.getBoundingClientRect().height
		}px)`;
	}, []);

	return (
		<div
			ref={ref}
			className={
				'w-full grid grid-cols-6 gap-6 pt-3' +
				(className ? ` ${className}` : '')
			}
		>
			{children && children}
		</div>
	);
};

export { PanelBody, PanelMain };
