import { useContext } from 'react';
import ResourceDataInterface from '../../interfaces/ResourceDataInterface';
import { DOWNLOAD_LIST_LIMIT } from '../../utils/limits-util';
import AppContext from '../contexts/AppContext';
import { EditorInputType } from './editor-input';
import { v4 as uuidv4 } from 'uuid';

interface SceneProps {
	// id: string;
	display: string;
	previewImage: String | Blob;
	sceneObject: JSX.Element | null;
	editorInputs: {
		key: { internal: string; display: string };
		type: EditorInputType;
	}[];
}

export class Scene {
	id: string;
	display: string;
	previewImage: String | Blob;
	sceneObject: JSX.Element | null;
	editorInputs: {
		key: { internal: string; display: string };
		type: EditorInputType;
	}[];

	constructor({
		display,
		previewImage,
		sceneObject,
		editorInputs,
	}: SceneProps) {
		// this.id = nanoid(12);
		this.id = uuidv4();
		this.display = display;
		this.previewImage = previewImage;
		this.sceneObject = sceneObject;
		this.editorInputs = editorInputs;
	}

	// isActiveScene(sceneManager: SceneManager) {
	// 	this.id == sceneManager.activeScene;
	// }
}

type SceneManagerAction =
	| {
			type: 'ADD';
			payload: Scene;
	  }
	| { type: 'REMOVE'; payload: Scene | string }
	| { type: 'CLEAR'; payload: undefined }
	| { type: 'GET_ALL'; payload: undefined }
	| { type: 'GET_ACTIVE'; payload: undefined }
	| { type: 'SET'; payload: Scene }
	| { type: 'SET_ACTIVE'; payload: Scene | string };

const SceneManagerReducer = (
	state: { activeScene: Scene; scenes: Scene[] },
	action: SceneManagerAction
) => {
	const appContext = useContext(AppContext);

	switch (action.type.toUpperCase()) {
		case 'ADD':
			return {
				activeScene: state.activeScene,
				scenes: [...state.scenes, action.payload],
			};

			console.log('ADDING TO LIST', action.payload);

			return [...state, action.payload];
		case 'REMOVE':
			console.log('REMOVING FROM LIST', action.payload);

			return [...state].filter((listItem) => listItem.id != action.payload.id);
		case 'CLEAR':
			console.log('CLEARING LIST', action.payload);

			return [];
		case 'SET':
			console.log('SETTING LIST', action.payload);
			return [...action.payload];
		default:
			return [...state];
	}
};

export type { SceneManagerAction };
export { SceneManagerReducer };
