import Link from 'next/link';
import React, { createRef, useEffect } from 'react';
import Header2 from '../components/Header';

const FourOFourApp = () => {
	const headerRef = createRef<HTMLDivElement>();
	const containerRef = createRef<HTMLDivElement>();

	//FIX OFFSET HEIGHT SCROLLBAR ISSUE
	useEffect(() => {
		containerRef.current!.style.height = `calc(100vh - ${
			headerRef.current!.getBoundingClientRect().height
		}px)`;
	}, []);

	return (
		<div className="h-full">
			<Header2 ref={headerRef} />
			<div
				ref={containerRef}
				className="px-18.75% py-20 flex flex-col h-full items-center justify-center"
			>
				<h1 className="text-5xl">404</h1>
				<h3 className="font-normal mt-3">
					The requested page has not been found.
				</h3>
				<Link href="/">
					<button className="primary mt-6">Return to home</button>
				</Link>
			</div>
		</div>
	);
};

export default FourOFourApp;
