import React, { createRef, useEffect } from 'react';
import Header from '../../components/Header';
import { Panel } from '../../components/Panel';
import faker from 'faker';
import Link from 'next/link';
import { Button } from '../../components/Button';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
	faComment,
	faPaperPlane,
	faStar,
} from '@fortawesome/free-solid-svg-icons';
import { useRouter } from 'next/router';

const ProfileApp = () => {
	const headerRef = createRef<HTMLDivElement>();
	const containerRef = createRef<HTMLDivElement>();

	const router = useRouter();

	useEffect(() => {
		containerRef.current!.style.height = `calc(100vh - ${
			headerRef.current!.getBoundingClientRect().height
		}px)`;
	}, []);

	return (
		<div className="h-full">
			<Header ref={headerRef} />
			<div
				ref={containerRef}
				className="px-12.5% py-12 overflow-hidden overflow-y-scroll"
			>
				<div className="flex items-center justify-between">
					<div className="flex items-center space-x-6">
						<img
							src={faker.image.imageUrl()}
							className="w-20 h-20 rounded-md object-cover"
						/>
						<div>
							<h2>RiznSun</h2>
							<p className="mt-1">No profile description {router.asPath}</p>
						</div>
					</div>
					<div className="flex items-center space-x-3">
						<Button
							// compact={true}
							icon={<FontAwesomeIcon icon={faPaperPlane} />}
						>
							Message
						</Button>
						<Button
							// compact={true}
							icon={<FontAwesomeIcon icon={faStar} />}
						>
							Follow
						</Button>
					</div>
				</div>
				<div className="mt-6">
					<ul className="flex items-center space-x-8">
						{[
							{ name: 'Overview', link: `/users/${router.query.id}` },
							{
								name: 'Contact',
								link: `/users/${router.query.id}?tab=contact`,
							},
							{
								name: 'Reputation',
								link: `/users/${router.query.id}?tab=reputation`,
							},
							{
								name: 'Wallets',
								link: `/users/${router.query.id}?tab=wallets`,
							},
							{
								name: 'Resources',
								link: `/users/${router.query.id}?tab=resources`,
							},
							{
								name: 'Business Accounts',
								link: `/users/${router.query.id}?tab=accounts`,
							},
						].map((link) => {
							console.log('pre', router.asPath);
							console.log('post', link.link);
							console.log('match', router.asPath == link.link);
							return (
								<li
									className={
										(router.asPath == link.link
											? 'text-primary-color font-medium border-primary-color border-b-2'
											: '') + ' pb-3'
									}
								>
									<Link href={link.link}>
										<a>{link.name}</a>
									</Link>
								</li>
							);
						})}
					</ul>
					<hr />
				</div>
				<div className="pt-6 grid grid-cols-7 gap-6">
					<div className="col-span-5">
						<Panel compact={true} header={{ content: 'Profile Posts' }}>
							<ul className="">
								{[
									{
										name: faker.name.findName(),
										content: faker.lorem.text(),
									},
									{
										name: faker.name.findName(),
										content: faker.lorem.text(),
									},
									{
										name: faker.name.findName(),
										content: faker.lorem.text(),
									},
								].map((post) => {
									return (
										<li className="border-b-1 border-primary-border py-6 first:pt-0 last:border-b-0 last:pb-0">
											<div className="flex items-center space-x-3">
												<img
													src={faker.image.imageUrl()}
													className="w-10 h-10 rounded-sm"
												/>
												<p className="flex flex-col">
													<Link href="/users/1">
														<a className="text-yellow-400 font-medium">
															{post.name}
														</a>
													</Link>
													<span>10 days ago</span>
												</p>
											</div>
											<p className="overflow-hidden overflow-ellipse line-clamp-4 mt-2 text-white">
												{/* {post.content} */}
												Lorem ipsum dolor sit amet, consectetur adipiscing elit.
												Proin facilisis libero sit amet lobortis pellentesque.
												Integer congue eros sit amet malesuada ornare. Quisque
												convallis suscipit tellus, ac feugiat nisi lobortis
												quis. Vestibulum tempor justo id posuere aliquet. Nulla
												maximus erat dolor, eget euismod dui mattis quis.
												Vivamus velit enim, sollicitudin sit amet tellus vitae,
												pulvinar lacinia arcu. Sed vel sapien in mi elementum
												lacinia. Suspendisse molestie nisl a eros ultrices
												euismod. Donec eget faucibus lacus, et pretium dui.
												Proin dui lacus, lacinia sed lacus molestie, tempus
												mollis velit. Phasellus ut pretium libero. Pellentesque
												laoreet dolor libero, sit amet accumsan neque placerat
												eu. Sed eget tempus turpis, nec cursus felis.
												Pellentesque at nisl ac ante maximus feugiat aliquam
												vitae felis.
											</p>
										</li>
									);
								})}
							</ul>
						</Panel>
					</div>
					<div className="col-span-2">
						<Panel compact={true} header={{ content: 'Reputation Overview' }}>
							<div className="flex items-center justify-evenly">
								<div className="w-full flex items-center justify-center">
									<p className="text-positive-cta font-medium">98% Positive</p>
								</div>
								{/* <span
								className="border-1 border-primary-border"
								style={{ width: '1px' }}
							/> */}
								<div className="border-l-1 border-primary-border w-full flex items-center justify-center">
									<p className="text-negative-cta font-medium">2% Negative</p>
								</div>
							</div>
							<Link href={`/users/${router.query.id}?tab=reputation`} passHref>
								<Button
									outline={true}
									compact={true}
									className="w-full justify-center mt-6"
								>
									View all reputation
								</Button>
							</Link>
						</Panel>
					</div>
				</div>
			</div>
		</div>
	);
};

