import {
	faBell,
	faBoxOpen,
	faExternalLinkAlt,
	faPaperPlane,
	IconDefinition,
} from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import Link from 'next/link';
import React, { ReactNode } from 'react';
import { createRef, useEffect } from 'react';
// import { AlertCircle, CheckCircle } from 'react-feather';
import Header from '../components/Header';
import { Panel } from '../components/Panel';
import Sidebar from '../components/Sidebar';
import { default as faker } from 'faker';
import { Button } from '../components/Button';
// import TemplateOne from '../components/templates/Template1';
// import TemplateTwo from '../components/templates/Template2';
// import TemplateThree from '../components/templates/Template3';

const CHAT_MESSAGES: {
	author: { name: string; icon: string };
	content: string;
}[] = [
	{
		author: {
			name: faker.name.findName(),
			// icon: 'https://cdn.discordapp.com/avatars/798294419904004166/00030c0c0dcd5da64513327db5e219ed.webp?size=256',
			icon: faker.image.imageUrl(),
		},
		content: faker.lorem.text().trim().substring(0, 180),
	},
	{
		author: {
			name: faker.name.findName(),
			// icon: 'https://cdn.discordapp.com/avatars/798294419904004166/00030c0c0dcd5da64513327db5e219ed.webp?size=256',
			icon: faker.image.imageUrl(),
		},
		content: faker.lorem.text().trim().substring(0, 180),
	},
	{
		author: {
			name: faker.name.findName(),
			// icon: 'https://cdn.discordapp.com/avatars/798294419904004166/00030c0c0dcd5da64513327db5e219ed.webp?size=256',
			icon: faker.image.imageUrl(),
		},
		content: faker.lorem.text().trim().substring(0, 180),
	},
	{
		author: {
			name: faker.name.findName(),
			// icon: 'https://cdn.discordapp.com/avatars/798294419904004166/00030c0c0dcd5da64513327db5e219ed.webp?size=256',
			icon: faker.image.imageUrl(),
		},
		content: faker.lorem.text().trim().substring(0, 180),
	},
	{
		author: {
			name: faker.name.findName(),
			// icon: 'https://cdn.discordapp.com/avatars/798294419904004166/00030c0c0dcd5da64513327db5e219ed.webp?size=256',
			icon: faker.image.imageUrl(),
		},
		content: faker.lorem.text().trim().substring(0, 180),
	},
];

const MAIN_CATEGORIES: {
	name: string;
	description: string;
	icon: IconDefinition;
}[] = [
	{ name: 'Announcements', description: '', icon: faBell },
	{ name: 'General Discussion', description: '', icon: faBell },
	{ name: 'Appraisals', description: '', icon: faBell },
	{ name: 'Feedback', description: '', icon: faBell },
];

const HomeApp = ({ children }: { children?: ReactNode }) => {
	const headerRef = createRef<HTMLDivElement>();
	const containerRef = createRef<HTMLDivElement>();

	useEffect(() => {
		containerRef.current!.style.height = `calc(100vh - ${
			headerRef.current!.getBoundingClientRect().height
		}px)`;
	}, []);

	return (
		<div className="h-full">
			<Header ref={headerRef} />
			<div
				ref={containerRef}
				className="px-12.5% py-12 grid grid-cols-7 gap-6 overflow-hidden overflow-y-scroll"
			>
				<div className="col-span-5 space-y-6">
					<Panel
						style={{ maxHeight: '650px' }}
						className="overflow-hidden"
						noPadding={true}
					>
						<div className="flex items-center justify-between px-4 pt-4">
							<h4>Chat Room</h4>
							<a href="/" target="_blank">
								<FontAwesomeIcon icon={faExternalLinkAlt} className="mr-2" />
								Open in new window
							</a>
						</div>
						<hr className="mt-3" />
						<div className="grid grid-cols-5 gap-6">
							<div className="col-span-4 overflow-hidden relative">
								<ul className="space-y-6 overflow-y-scroll pt-6 px-6">
									{CHAT_MESSAGES.map((msg) => {
										return (
											<li className="w-full overflow-hidden truncate">
												<div className="flex space-x-2">
													{/* <div className="min-w-6 min-h-6 w-6 h-6 bg-tertiary-bg rounded-full"></div> */}
													<img
														src={msg.author.icon}
														className="w-6 h-6 rounded-full"
													/>
													<div>
														<h6>{msg.author.name}</h6>
														<div className="bg-tertiary-bg p-3 mt-2 rounded-sm overflow-hidden truncate">
															<p className="text-primary-text text-sm truncate">
																{msg.content}
															</p>
														</div>
													</div>
												</div>
											</li>
										);
									})}
								</ul>
								<div className="absolute left-0 right-0 bottom-0 bg-tertiary-bg w-full p-3 flex items-center justify-between">
									<input
										placeholder="Type a chat message..."
										className="bg-transparent w-full"
									/>
									{/* <button className="">Send</button> */}
									<Button
										compact={true}
										icon={<FontAwesomeIcon icon={faPaperPlane} />}
									>
										Send
									</Button>
								</div>
							</div>
							<div>
								<ul className="space-y-3 pt-6">
									{Array(4)
										.fill(null)
										.map(() => {
											return {
												name: faker.name.findName(),
												icon: faker.image.imageUrl(),
											};
										})
										.map((user) => {
											return (
												<li className="flex items-center gap-2 text-white font-medium">
													<img
														src={user.icon}
														className="w-6 h-6 rounded-full"
													/>
													{user.name}
												</li>
											);
										})}
								</ul>
							</div>
						</div>
						<div></div>
					</Panel>
					<Panel compact={true}>
						<h5>Main Categories</h5>
						<hr className="my-3" />
						<div className="space-y-3">
							{MAIN_CATEGORIES.map((cat) => {
								return (
									<Panel compact={true} className="bg-none border-b-1">
										<h5>{cat.name}</h5>
									</Panel>
								);
							})}
						</div>
					</Panel>
				</div>
				<div className="col-span-2 space-y-6">
					<Panel
						compact={true}
						header={{ transparent: true, content: 'Latest Posts' }}
					>
						<ul className="space-y-4">
							{Array(5)
								.fill(null)
								.map((user) => {
									return (
										<li className="flex space-x-3">
											<img
												src={faker.image.imageUrl()}
												className="w-10 h-10 rounded-sm"
											/>
											<div className="overflow-hidden">
												<Link href="https://discord.gg/eEFcyVfFU2">
													<h6
														className="font-medium w-full truncate cursor-pointer hover:underline"
														// style={{ margin: '0px !important;' }}
													>
														{faker.commerce.productDescription()}
													</h6>
												</Link>
												<Link href="https://discord.gg/eEFcyVfFU2">
													<p>
														From{' '}
														<a target="_blank" className="">
															<span className="text-yellow-400 font-medium">
																RiznSun
															</span>
														</a>{' '}
														about 3 minutes ago
													</p>
												</Link>
											</div>
										</li>
									);
								})}
						</ul>
					</Panel>
				</div>
			</div>
		</div>
	);
};

export default HomeApp;
