import { useContext } from 'react';
import DiscordStatus from '../../react/discord-status';
import EditorContext from '../../react/editor-context';

const PreviewStatusBar = ({
	className,
	onChange,
}: {
	className?: string;
	onChange?: (status: DiscordStatus) => any;
}) => {
	const editorContext = useContext(EditorContext);

	return (
		<div
			className={
				'bg-secondary-bg rounded-full py-2 pl-6 pr-2 flex items-center space-x-3 select-none ' +
				(className || '')
			}
		>
			<p className="text-sm">PREVIEW FOR:</p>
			{[
				DiscordStatus.ONLINE,
				DiscordStatus.IDLE,
				DiscordStatus.DND,
				DiscordStatus.OFFLINE,
			].map((status) => {
				return (
					<div
						className={
							'text-sm flex items-center space-x-2 p-2 rounded-full cursor-pointer' +
							(editorContext.previewStatus &&
							editorContext.previewStatus.value.display == status.value.display
								? ' bg-quartiary-bg'
								: '')
						}
						onClick={() => {
							if (
								editorContext.previewStatus &&
								editorContext.previewStatus.value.display ==
									status.value.display
							) {
								return;
							}
							onChange && onChange(status);
							editorContext.setPreviewStatus(status);
						}}
					>
						{status.value.htmlIcon}
						<p className="pr-1">{status.value.display}</p>
					</div>
				);
			})}
		</div>
	);
};

export default PreviewStatusBar;
