import { EditorInputType } from './editor-input';
import { v4 as uuidv4 } from 'uuid';

interface SceneProps {
	// id: string;
	display: string;
	previewImage: String | Blob;
	sceneObject: JSX.Element | null;
	editorInputs: {
		key: { internal: string; display: string };
		type: EditorInputType;
	}[];
}

export class Scene {
	id: string;
	display: string;
	previewImage: String | Blob;
	sceneObject: JSX.Element | null;
	editorInputs: {
		key: { internal: string; display: string };
		type: EditorInputType;
	}[];

	constructor({
		display,
		previewImage,
		sceneObject,
		editorInputs,
	}: SceneProps) {
		// this.id = nanoid(12);
		this.id = uuidv4();
		this.display = display;
		this.previewImage = previewImage;
		this.sceneObject = sceneObject;
		this.editorInputs = editorInputs;
	}

	// isActiveScene(sceneManager: SceneManager) {
	// 	this.id == sceneManager.activeScene;
	// }
}

type SceneManagerAction =
	| {
			type: 'ADD';
			payload: Scene;
	  }
	| { type: 'REMOVE'; payload: Scene | string }
	| { type: 'CLEAR'; payload: undefined }
	| { type: 'GET_ALL'; payload: undefined }
	| { type: 'GET_ACTIVE'; payload: undefined }
	| { type: 'SET'; payload: Scene }
	| { type: 'SET_ACTIVE'; payload: Scene | string };

const SceneManagerReducer = (
	state: { activeScene: Scene | null; scenes: Scene[] },
	action: SceneManagerAction
) => {
	switch (action.type.toUpperCase()) {
		case 'ADD':
			console.log('ADDING TO SCENES', action.payload);

			return {
				activeScene: state.activeScene,
				scenes: [...state.scenes, action.payload],
			};
		case 'REMOVE':
			console.log('REMOVING FROM SCENES', action.payload);

			return {
				activeScene: state.activeScene,
				scenes: [...state.scenes].filter(
					(scene) =>
						scene.id ==
						(action.payload instanceof String
							? action.payload
							: (action.payload as Scene).id)
				),
			};
		case 'CLEAR':
			console.log('CLEARING SCENES', action.payload);

			return {
				activeScene: null,
				scenes: [],
			};
		case 'SET':
			console.log('SETTING SCENES', action.payload);

			return {
				activeScene: null,
				scenes: action.payload,
			};
		default:
			return state;
	}
};

export type { SceneManagerAction };
export { SceneManagerReducer };
