import React, { useEffect } from 'react';
import { Minus, MinusCircle } from 'react-feather';
import { default as tailwind } from '../../tailwind.config.js';
var app = require('node-server-screenshot');

enum Badge {
	HYPESQUAD_BRAVERY = 'https://discordia.me/uploads/badges/bravery_badge.png',
	NITRO = 'https://discordia.me/uploads/badges/nitro_badge.png',
}

const screenshotTest = () => {
	app.fromHTML(<TemplateThree className="mr-3" />, 'test.png', function () {
		//an image of the HTML has been saved at ./test.png
		console.log('saved');
	});
};

const TemplateThree = ({ className }: { className: string }) => {
	useEffect(() => {
		screenshotTest();
	}, []);

	return (
		<div
			className={
				'bg-tertiary-bg relative overflow-hidden flex flex-col justify-between ' +
				className
			}
			style={{ width: '976px', height: '244px' }}
		>
			<div className="template-two-bg w-full h-full p-6 flex items-center justify-center gap-6">
				<img
					className="rounded-full"
					style={{ width: '90px', height: '90px' }}
					src="https://cdn.discordapp.com/avatars/670427777757085730/999b6917c1912a51b4c7e1b05f21cee9.png?size=256"
				/>
				<div>
					<h3 className="text-secondary-text font-medium flex items-center gap-1">
						RiznSun<span className="text-gray-300">#7591</span>
					</h3>
					{/* <div className="flex items-center p-2 rounded-full gap-3 bg-primary-bg w-fit">
						{[Badge.HYPESQUAD_BRAVERY, Badge.NITRO].map((badge) => {
							return <img style={{ width: '20px' }} src={badge} />;
						})}
					</div> */}
					<p className="flex items-center text-red-500 font-medium mt-1 gap-2">
						{/* <span className="text-secondary-text font-bold">Status:</span> */}
						<span className="font-normal flex items-center text-red-500">
							<div
								className="w-5 h-5 bg-red-500 rounded-full font-mono flex place-items-center"
								style={{}}
							>
								<Minus color={'black'} strokeWidth={3} />
							</div>
						</span>
						Do Not Disturb
					</p>
				</div>
			</div>
			<div className="bg-quartiary-bg bg-opacity-40 w-full py-2 text-center">
				<p>Add your sick, custom description tag here.</p>
			</div>
			<div className="absolute top-4 right-4 uppercase text-sm bg-quartiary-bg py-1 px-2 rounded-full opacity-70">
				<p>Powered by REDACTED</p>
			</div>
		</div>
	);
};

export default TemplateThree;
