import { Icon as FeatherIcon } from 'react-feather';
import Link from 'next/link';
import { useRouter } from 'next/router';
import React from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
	faHome,
	faTerminal,
	faScroll,
} from '@fortawesome/free-solid-svg-icons';

const NAV_LINKS: {
	name: string;
	href: string;
	icon?: JSX.Element;
}[] = [
	{
		name: 'Dashboard',
		href: '/',
		icon: <FontAwesomeIcon icon={faHome} className="mr-3" />,
	},
	{
		name: 'Commands',
		href: '/commands',
		icon: <FontAwesomeIcon icon={faTerminal} className="mr-3" />,
	},
	{ name: 'Modules', href: '/modules' },
	{
		name: 'Logs',
		href: '/logs',
		icon: <FontAwesomeIcon icon={faScroll} className="mr-3" />,
	},
];

const Sidebar = () => {
	const currentHref = useRouter().pathname;
	console.log('CURRENT HREF', currentHref);
	return (
		// <div className="bg-secondary-bg h-full p-6" style={{ width: '15%' }}></div>
		<div className="p-3 w-full h-full">
			<ul className="space-y-2 w-full">
				{NAV_LINKS.map((navLink) => {
					return (
						<li
							className={
								'py-3 px-4 rounded-md font-medium cursor-pointer' +
								((
									navLink.href.length > 1
										? currentHref.startsWith(navLink.href)
										: currentHref == navLink.href
								)
									? ' text-primary-text bg-primary-color bg-opacity-75'
									: ' hover:bg-primary-color hover:bg-opacity-20')
							}
						>
							{navLink.icon && navLink.icon}
							<Link href={navLink.href}>{navLink.name}</Link>
						</li>
					);
				})}
			</ul>
		</div>
	);
};

export default Sidebar;
