import React, { createRef, useEffect } from 'react';
import Header from '../../components/Header';
import { Panel } from '../../components/Panel';
import faker from 'faker';
import Link from 'next/link';
import { Button } from '../../components/Button';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
	faComment,
	faPaperPlane,
	faStar,
} from '@fortawesome/free-solid-svg-icons';
import { useRouter } from 'next/router';
import OverviewSubApp from './OverviewSubApp';
import WalletsSubApp from './WalletsSubApp';
import UserLink from '../../components/UserLink';
import ContactSubApp from './ContactSubApp';
import ReputationSubApp from './ReputationSubApp';
import ResourcesSubApp from './ResourcesSubApp';
import AccountsSubApp from './AccountsSubApp';

const ProfileApp = () => {
	const headerRef = createRef<HTMLDivElement>();
	const containerRef = createRef<HTMLDivElement>();

	const router = useRouter();
	const tab = router.query.tab || '';

	useEffect(() => {
		containerRef.current!.style.height = `calc(100vh - ${
			headerRef.current!.getBoundingClientRect().height
		}px)`;
	}, []);

	return (
		<div className="h-full">
			<Header ref={headerRef} />
			<div
				ref={containerRef}
				className="px-12.5% py-12 overflow-hidden overflow-y-scroll"
			>
				<div className="flex items-center justify-between">
					<div className="flex items-center space-x-6">
						<img
							src={faker.image.imageUrl()}
							className="w-20 h-20 rounded-md object-cover"
						/>
						<div>
							<div className="flex items-center space-x-3">
								<UserLink>
									<h4>RiznSun</h4>
								</UserLink>

								<span className="block w-fit bg-negative-cta px-2 py-1 rounded-lg text-primary-text">
									Owner
								</span>
							</div>
							<p className="mt-1">Software Engineer</p>
						</div>
					</div>
					<div className="flex items-center space-x-3">
						<Button
							// compact={true}
							icon={<FontAwesomeIcon icon={faPaperPlane} />}
						>
							Message
						</Button>
						<Button
							// compact={true}
							icon={<FontAwesomeIcon icon={faStar} />}
						>
							Follow
						</Button>
					</div>
				</div>
				<div className="mt-6">
					<ul className="flex items-center space-x-8">
						{[
							{ name: 'Overview', link: `/users/${router.query.id}` },
							{
								name: 'Contact',
								link: `/users/${router.query.id}?tab=contact`,
							},
							{
								name: 'Reputation',
								link: `/users/${router.query.id}?tab=reputation`,
							},
							{
								name: 'Wallets',
								link: `/users/${router.query.id}?tab=wallets`,
							},
							{
								name: 'Resources',
								link: `/users/${router.query.id}?tab=resources`,
							},
							{
								name: 'Business Accounts',
								link: `/users/${router.query.id}?tab=accounts`,
							},
						].map((link) => {
							console.log('pre', router.asPath);
							console.log('post', link.link);
							console.log('match', router.asPath == link.link);
							return (
								<li
									className={
										(router.asPath == link.link
											? 'text-primary-color font-medium border-primary-color border-b-2'
											: '') + ' pb-3'
									}
								>
									<Link href={link.link}>
										<a>{link.name}</a>
									</Link>
								</li>
							);
						})}
					</ul>
					<hr />
				</div>
				{!tab && <OverviewSubApp />}
				{tab == 'contact' && <ContactSubApp />}
				{tab == 'reputation' && <ReputationSubApp />}
				{tab == 'wallets' && <WalletsSubApp />}
				{tab == 'resources' && <ResourcesSubApp />}
				{tab == 'accounts' && <AccountsSubApp />}
			</div>
		</div>
	);
};

