import {
	faBoxOpen,
	faExternalLinkAlt,
} from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import Link from 'next/link';
import React, { ReactNode } from 'react';
import { createRef, useEffect } from 'react';
// import { AlertCircle, CheckCircle } from 'react-feather';
import Header from '../components/Header';
import { Panel } from '../components/Panel';
import Sidebar from '../components/Sidebar';
// import TemplateOne from '../components/templates/Template1';
// import TemplateTwo from '../components/templates/Template2';
// import TemplateThree from '../components/templates/Template3';

const HomeApp = ({ children }: { children?: ReactNode }) => {
	const headerRef = createRef<HTMLDivElement>();
	const containerRef = createRef<HTMLDivElement>();

	return (
		<div className="h-full">
			<Header ref={headerRef} />
			<div ref={containerRef} className="px-12.5% pt-12 grid grid-cols-7 gap-6">
				<div className="col-span-5">
					<Panel>
						<div className="flex items-center justify-between">
							<h4>Chat Room</h4>
							<a href="/" target="_blank">
								<FontAwesomeIcon icon={faExternalLinkAlt} />
								Open in new window
							</a>
						</div>
						<hr className="my-3" />
						<div></div>
					</Panel>
				</div>
				<div className="col-span-2">
					<Panel>
						<h4>Recent Posts</h4>
					</Panel>
				</div>
			</div>
		</div>
	);
};

export default HomeApp;
