import React, { createRef, useEffect } from 'react';
import Header from '../components/Header';

const ThreadApp = () => {
	const headerRef = createRef<HTMLDivElement>();
	const containerRef = createRef<HTMLDivElement>();

	useEffect(() => {
		containerRef.current!.style.height = `calc(100vh - ${
			headerRef.current!.getBoundingClientRect().height
		}px)`;
	}, []);

	return (
		<div className="h-full">
			<Header ref={headerRef} />
			<div
				ref={containerRef}
				className="px-12.5% py-12 overflow-hidden overflow-y-scroll"
			>
				<div className="w-full pb-6 flex items-center justify-between">
					<div>
						<h3>I need a developer</h3>
						{/* <p className="text-lg mt-1">
							THREADS / DEVELOPMENT
						</p> */}
						<div>
							<div className="w-12 h-12 bg-secondary-bg"></div>
						</div>
					</div>
				</div>
			</div>
		</div>
	);
};
export default ThreadApp;
