// const colors = require('tailwindcss/colors')

module.exports = {
	// purge: ['./components/**/*.{js,ts,jsx,tsx}', './pages/**/*.{js,ts,jsx,tsx}'],
	darkMode: 'media',
	variants: {
		extend: {
			textColor: ['responsive', 'hover', 'group-hover', 'focus'],
		},
	},
	theme: {
		extend: {
			backgroundImage: (theme) => ({
				'test-banner':
					"url('https://images.unsplash.com/photo-1486607303850-bc051a4ffad4?ixid=MXwxMjA3fDB8MHx0b3BpYy1mZWVkfDJ8Ym84alFLVGFFMFl8fGVufDB8fHw%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=800&q=60')",
				// 'footer-texture': "url('/img/footer-texture.png')",
			}),
			fontFamily: {
				heading: ['Inter', 'sans-serif'],
				base: ['Roboto', 'sans-serif'],
			},
			colors: {
				// gray: colors.trueGray,
				'primary-bg': '#23272A',
				// 'secondary-bg': '#23272A',
				'secondary-bg': '#36393F',
				'tertiary-bg': '#36393F',
				'quartiary-bg': '#595e68',
				'primary-color': '#2A5EE8',
				'primary-border': '#36393F',
				// 'primary-border': '#7b7d7f',
				'primary-text': 'white',
				'secondary-text': '#c8c8ca',
				// 'secondary-text': '#B6C0DE',
			},
			borderWidth: {
				3: '3px',
			},
			padding: {
				'12.5%': '12.5%',
				'18.75%': '18.75%',
				'25%': '25%',
			},
			margin: {
				'12.5%': '12.5%',
				'18.75%': '18.75%',
			},
			borderWidth: {
				1: '1px',
			},
			height: {
				fit: 'fit-content',
			},
			width: {
				fit: 'fit-content',
			},
		},
		boxShadow: {
			xs: '0 0 0 1px rgba(0, 0, 0, 0.05)',
			sm: '0 0px 2px 0 rgba(0, 0, 0, 0.05)',
			default:
				'0 0px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px 0 rgba(0, 0, 0, 0.06)',
			md:
				'0 0px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06)',
			lg:
				'0 0px 15px 5px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05)',
			xl:
				'0 0px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04)',
			'2xl': '0 0px 50px -12px rgba(0, 0, 0, 0.25)',
			'3xl': '0 0px 60px -15px rgba(0, 0, 0, 0.3)',
			inner: 'inset 0 0px 4px 0 rgba(0, 0, 0, 0.06)',
			outline: '0 0 0 3px rgba(66, 153, 225, 0.5)',
			focus: '0 0 0 3px rgba(66, 153, 225, 0.5)',
			none: 'none',
		},
	},
	plugins: [require('@tailwindcss/forms'), require('tailwind-scrollbar')],
};
