import { faDiscord } from '@fortawesome/free-brands-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import React, { forwardRef } from 'react';
// import { Link } from 'react-feather';
import Link from 'next/link';
import { useRouter } from 'next/router';

const Header2 = forwardRef(
	(
		props: { children?: React.ReactNode },
		ref: React.ForwardedRef<HTMLDivElement>
	) => {
		const currentHref = useRouter().pathname;
		props;
		console.log('CURRENT HREF', currentHref);

		return (
			<div
				ref={ref}
				className={
					'w-full px-6 bg-primary-bg flex items-center justify-between' +
					props.border
						? ' border-b-1 border-primary-border'
						: ''
				}
				style={{ height: '78px', maxHeight: '85px' }}
			>
				<div className="flex flex-col py-4">
					<Link href="/">
						<h2 className="cursor-pointer font-extrabold">
							{/* <span className="text-primary-color">Redacted</span>Name */}
							StatusCord
						</h2>
					</Link>

					{/* <p className="text-secondary-text uppercase">The #1 Build Store</p> */}
				</div>
				<div className="h-full">
					<ul className="flex items-center gap-6 h-full">
						{[
							{ display: 'Home', href: '/' },
							{ display: 'Templates', href: '/templates' },
							{ display: 'Plans', href: '/plans' },
							{ display: 'Support', href: '/support' },
						].map((link) => {
							return (
								<Link href={link.href}>
									<a
										className={
											'h-full flex items-center px-3' +
											((
												link.href.length > 1
													? currentHref.startsWith(link.href)
													: currentHref == link.href
											)
												? ' text-highlight-text border-b-2 border-highlight-text'
												: ' hover:text-highlight-text')
										}
									>
										{link.display}
									</a>
								</Link>
							);
						})}
					</ul>
				</div>
				<div>
					<button
						className="py-2 px-4 flex items-center text-white rounded-md bg-primary-color"
						// style={{ backgroundColor: '#7289DA' }}
					>
						<FontAwesomeIcon icon={faDiscord} />
						<span className="ml-2">Join our discord</span>
					</button>
				</div>
			</div>
		);
	}
);

export default Header2;
