import {
	faBell,
	faBoxOpen,
	faBullhorn,
	faCode,
	faCoins,
	faComments,
	faCube,
	faExternalLinkAlt,
	faInfo,
	faLandmark,
	faListAlt,
	faPaintBrush,
	faPaperPlane,
	faPeopleArrows,
	faSignInAlt,
	faSignOutAlt,
	faStream,
	faTags,
	faTerminal,
	IconDefinition,
} from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import Link from 'next/link';
import React, { ReactNode } from 'react';
import { createRef, useEffect } from 'react';
// import { AlertCircle, CheckCircle } from 'react-feather';
import Header from '../components/Header';
import { Panel } from '../components/Panel';
import Sidebar from '../components/Sidebar';
import { default as faker } from 'faker';
import { Button } from '../components/Button';
import { faIdBadge } from '@fortawesome/free-regular-svg-icons';
import UserLink from '../components/UserLink';
import { faDiscord } from '@fortawesome/free-brands-svg-icons';
// import TemplateOne from '../components/templates/Template1';
// import TemplateTwo from '../components/templates/Template2';
// import TemplateThree from '../components/templates/Template3';

const CHAT_MESSAGES: {
	author: { name: string; icon: string };
	content: string;
}[] = [
	{
		author: {
			name: faker.name.findName(),
			// icon: 'https://cdn.discordapp.com/avatars/798294419904004166/00030c0c0dcd5da64513327db5e219ed.webp?size=256',
			icon: faker.image.imageUrl(),
		},
		content: faker.lorem.text().trim().substring(0, 180),
	},
	{
		author: {
			name: faker.name.findName(),
			// icon: 'https://cdn.discordapp.com/avatars/798294419904004166/00030c0c0dcd5da64513327db5e219ed.webp?size=256',
			icon: faker.image.imageUrl(),
		},
		content: faker.lorem.text().trim().substring(0, 180),
	},
	{
		author: {
			name: faker.name.findName(),
			// icon: 'https://cdn.discordapp.com/avatars/798294419904004166/00030c0c0dcd5da64513327db5e219ed.webp?size=256',
			icon: faker.image.imageUrl(),
		},
		content: faker.lorem.text().trim().substring(0, 180),
	},
	{
		author: {
			name: faker.name.findName(),
			// icon: 'https://cdn.discordapp.com/avatars/798294419904004166/00030c0c0dcd5da64513327db5e219ed.webp?size=256',
			icon: faker.image.imageUrl(),
		},
		content: faker.lorem.text().trim().substring(0, 180),
	},
	{
		author: {
			name: faker.name.findName(),
			// icon: 'https://cdn.discordapp.com/avatars/798294419904004166/00030c0c0dcd5da64513327db5e219ed.webp?size=256',
			icon: faker.image.imageUrl(),
		},
		content: faker.lorem.text().trim().substring(0, 180),
	},
];

const MAIN_CATEGORIES: {
	name: string;
	description: string;
	icon: IconDefinition;
}[] = [
	{
		name: 'Announcements',
		description:
			'Hear the latest news about new features, staff changes, & more.',
		icon: faBell,
	},
	{ name: 'General Discussion', description: '', icon: faComments },
	{ name: 'Appraisals', description: '', icon: faTags },
	{ name: 'Feedback', description: '', icon: faPeopleArrows },
];

// const MARKETPLACE_CATEGORIES: {
// 	name: string;
// 	description: string;
// 	icon: IconDefinition;
// }[] = [
// 	{ name: 'Development', description: '', icon: faBell },
// 	{ name: 'Design', description: '', icon: faComments },
// 	{ name: 'Appraisals', description: '', icon: faTags },
// 	{ name: 'Feedback', description: '', icon: faPeopleArrows },
// ];

const SERVICES_CATEGORIES: {
	name: string;
	description: string;
	icon: IconDefinition;
}[] = [
	{ name: 'Development', description: '', icon: faCode },
	{ name: 'Design', description: '', icon: faPaintBrush },
	{ name: 'Building', description: '', icon: faCube },
	{ name: 'System Administration', description: '', icon: faTerminal },
	{ name: 'Staffing & Hiring', description: '', icon: faIdBadge },
	{ name: 'Advertising', description: '', icon: faBullhorn },
	{ name: 'Investments & Loans', description: '', icon: faLandmark },
	{ name: 'Currency Conversion', description: '', icon: faCoins },
	{ name: 'Middleman Services', description: '', icon: faPeopleArrows },
	{ name: 'Miscellaneous', description: '', icon: faInfo },
];

const SIDEBAR_ADS = [
	'https://www.mc-market.org/data/Siropu/images/2172695f516bc7e7d29.gif',
	'https://www.mc-market.org/data/Siropu/images/727365edef46fe07df.gif',
	'https://www.mc-market.org/data/Siropu/images/15405fa66fbb42393.gif',
	'https://www.mc-market.org/data/Siropu/images/960495fe696a8d6587.gif',
	'https://www.mc-market.org/data/Siropu/images/922825ffc917c1cf8c.gif',
];

const HomeApp = ({ children }: { children?: ReactNode }) => {
	const headerRef = createRef<HTMLDivElement>();
	const containerRef = createRef<HTMLDivElement>();

	useEffect(() => {
		containerRef.current!.style.height = `calc(100vh - ${
			headerRef.current!.getBoundingClientRect().height
		}px)`;
	}, []);

	return (
		<div className="h-full">
			<Header ref={headerRef} />
			<div
				ref={containerRef}
				className="px-12.5% py-12 overflow-hidden overflow-y-scroll"
			>
				<div className="w-full pb-6 flex items-center justify-between">
					<div>
						<h3>Welcome to Marketplace</h3>
						<p className="text-lg mt-1">
							No censorship and minimal rules. All around better than MC-Market.
						</p>
					</div>
					<div className="flex items-center space-x-3">
						<Button
							outline={true}
							compact={false}
							icon={<FontAwesomeIcon icon={faStream} />}
						>
							View Content Feed
						</Button>
						<Button
							compact={false}
							icon={<FontAwesomeIcon icon={faSignInAlt} />}
						>
							Sign Up Now
						</Button>
					</div>
				</div>
				<div className="grid grid-cols-7 gap-6">
					<div className="col-span-5 space-y-6 pb-12">
						<Panel
							style={{ maxHeight: '650px' }}
							className="overflow-hidden"
							noPadding={true}
						>
							<div className="flex items-center justify-between px-4 pt-4">
								<h4>Chat Room</h4>
								<a href="/chat" target="_blank">
									<FontAwesomeIcon icon={faExternalLinkAlt} className="mr-2" />
									Open in new window
								</a>
							</div>
							<hr className="mt-3" />
							<div className="grid grid-cols-5 gap-6 relative">
								<div className="col-span-4 overflow-hidden">
									<ul className="space-y-6 overflow-y-scroll pt-6 px-6">
										{CHAT_MESSAGES.map((msg) => {
											return (
												<li className="w-full overflow-hidden truncate">
													<div className="flex space-x-2">
														{/* <div className="min-w-6 min-h-6 w-6 h-6 bg-tertiary-bg rounded-full"></div> */}
														<img
															src={msg.author.icon}
															className="w-6 h-6 rounded-sm"
														/>
														<div>
															<h6>{msg.author.name}</h6>
															<div className="bg-tertiary-bg p-3 mt-2 rounded-sm overflow-hidden">
																<p className="text-primary-text text-sm truncate">
																	{msg.content}
																</p>
															</div>
														</div>
													</div>
												</li>
											);
										})}
									</ul>
									<div className="absolute left-0 right-0 bottom-0 bg-tertiary-bg w-full p-3 pl-5 flex items-center justify-between">
										<input
											placeholder="Type a chat message..."
											className="bg-transparent w-full"
										/>
										{/* <button className="">Send</button> */}
										<Button
											compact={true}
											icon={<FontAwesomeIcon icon={faPaperPlane} />}
										>
											Send
										</Button>
									</div>
								</div>
								<div>
									<ul className="space-y-3 pt-6">
										{Array(4)
											.fill(null)
											.map(() => {
												return {
													name: faker.name.findName(),
													icon: faker.image.imageUrl(),
												};
											})
											.map((user) => {
												return (
													<li className="flex items-center gap-2 text-white font-medium">
														<img
															src={user.icon}
															className="w-6 h-6 rounded-sm"
														/>
														{user.name}
													</li>
												);
											})}
									</ul>
								</div>
							</div>
							<div></div>
						</Panel>
						<Panel header={{ content: 'Main Categories' }}>
							<ul className="">
								{MAIN_CATEGORIES.map((cat) => {
									return (
										<li className="py-6 bg-none border-secondary-border border-b-2 first:pt-2 last:border-b-0 last:pb-4 rounded-b-none flex items-center justify-between">
											<div className="flex flex-col">
												<div className="flex items-center space-x-3 w-full">
													<div className="w-8 flex items-center justify-center">
														<FontAwesomeIcon
															icon={cat.icon}
															className="text-2xl text-primary-color"
														/>
													</div>
													<Link href="/threads/1">
														<a>
															<h5>{cat.name}</h5>
														</a>
													</Link>
												</div>
												{cat.description && (
													<div className="mt-2">
														<p className="mt-1">{cat.description}</p>
													</div>
												)}
											</div>
											<div className="px-3 py-2 border-2 border-secondary-border rounded-md w-1/3 text-sm">
												<p className="line-clamp-1 overflow-hidden w-full">
													New:{' '}
													<a className="text-primary-text">
														Changes to staffing, crypto, and more s soon to
														come.
													</a>
												</p>
												<p className="mt-1">
													<UserLink className="text-sm" />, 24 hours ago
												</p>
											</div>
										</li>
									);
								})}
							</ul>
						</Panel>
						<Panel
							compact={true}
							header={{ transparent: true, content: 'Services' }}
						>
							<div className="grid grid-cols-3 gap-3">
								{SERVICES_CATEGORIES.map((cat) => {
									return (
										<Panel compact={true} className="bg-tertiary-bg">
											<div className="flex items-center space-x-3 overflow-hidden">
												<div className="w-8 flex items-center justify-center">
													<FontAwesomeIcon
														icon={cat.icon}
														className="text-2xl text-primary-color"
													/>
												</div>
												<Link
													href={`/categories/${cat.name
														.toLowerCase()
														.replace(/ /g, '-')}`}
												>
													<a>
														<h5 className="truncate">{cat.name}</h5>
													</a>
												</Link>
												<p>{cat.description}</p>
											</div>
											<div className="border-b-2 border-secondary-border pb-3 text-sm mt-5">
												<p className="line-clamp-1 overflow-hidden w-full">
													New:{' '}
													<a className="text-primary-text">
														Lorem ipsum dolor imit. Who gives a fuck
													</a>
												</p>
												<p className="mt-1">
													<UserLink className="text-sm" />, 24 hours ago
												</p>
											</div>
											{/* <div className="px-3 py-2 border-2 border-secondary-border rounded-md text-sm mt-5">
												<p className="line-clamp-1 overflow-hidden w-full">
													New:{' '}
													<a className="text-primary-text">
														Lorem ipsum dolor imit. Who gives a fuck
													</a>
												</p>
												<p className="mt-1">
													<UserLink className="text-sm" />, 24 hours ago
												</p>
											</div> */}
										</Panel>
									);
								})}
							</div>
						</Panel>
					</div>
					<div className="col-span-2 space-y-6">
						<Panel
							transparent={true}
							innerClassName="space-y-2"
							noPadding={true}
						>
							{SIDEBAR_ADS.map((ad) => {
								return <img src={ad} />;
							})}
						</Panel>
						<Panel compact={true} header={{ content: 'Latest Posts' }}>
							<ul className="space-y-4">
								{Array(5)
									.fill(null)
									.map((user) => {
										return (
											<li className="flex space-x-3">
												<img
													src={faker.image.imageUrl()}
													className="w-10 h-10 rounded-sm"
												/>
												<div className="overflow-hidden">
													<Link href="/threads/1">
														<h6
															className="font-medium w-full truncate cursor-pointer hover:underline"
															// style={{ margin: '0px !important;' }}
														>
															{faker.commerce.productDescription()}
														</h6>
													</Link>
													<p>
														Post from <UserLink />, 3 minutes ago
													</p>
												</div>
											</li>
										);
									})}
							</ul>
						</Panel>
						<Panel compact={true} header={{ content: 'Community' }}>
							<p>Join our community discord for giveaways, events, & more.</p>
							<Button
								className="w-full justify-center mt-3"
								icon={<FontAwesomeIcon icon={faDiscord} />}
							>
								Join Discord
							</Button>
						</Panel>
					</div>
				</div>
			</div>
		</div>
	);
};

export default HomeApp;
