import React, { createRef, useEffect } from 'react';
import Header from '../components/Header';
import faker from 'faker';
import UserLink from '../components/UserLink';
import { Panel } from '../components/Panel';

const ThreadApp = () => {
	const headerRef = createRef<HTMLDivElement>();
	const containerRef = createRef<HTMLDivElement>();

	useEffect(() => {
		containerRef.current!.style.height = `calc(100vh - ${
			headerRef.current!.getBoundingClientRect().height
		}px)`;
	}, []);

	return (
		<div className="h-full">
			<Header ref={headerRef} />
			<div
				ref={containerRef}
				className="px-12.5% py-12 overflow-hidden overflow-y-scroll"
			>
				<div className="w-full pb-6 flex items-center justify-between">
					<div>
						<h3>I need a developer</h3>
						{/* <p className="text-lg mt-1">
							THREADS / DEVELOPMENT
						</p> */}
						<div className="mt-4 flex items-center space-x-3">
							<img
								src={faker.image.imageUrl()}
								className="w-12 h-12 rounded-sm"
							/>
							<div>
								<p className="text-sm uppercase">Posted by</p>
								<UserLink className="text-lg" />
							</div>
						</div>
					</div>
				</div>
				<hr />
				<Panel className="mt-6">
					<div className="bg-secondary-bg rounded-md grid grid-cols-5">
						<div className="col-span-1">
							<Panel className="bg-tertiary-bg"></Panel>
						</div>
						<div className="col-span-4">
							<img
								className="w-full"
								src="https://proxy.mc-market.org/756a64fce6d8c8ff166375b41296eeb90d72f5b9?url=https%3A%2F%2Fi.imgur.com%2F49rfBQX.png"
							/>
						</div>
					</div>
				</Panel>
			</div>
		</div>
	);
};
export default ThreadApp;
