module.exports = {
	// purge: ['./components/**/*.{js,ts,jsx,tsx}', './pages/**/*.{js,ts,jsx,tsx}'],
	darkMode: 'media',
	variants: {
		extend: {
			textColor: ['responsive', 'hover', 'group-hover', 'focus'],
		},
	},
	theme: {
		extend: {
			fontFamily: {
				heading: ['Inter', 'sans-serif'],
				base: ['Roboto', 'sans-serif'],
			},
			colors: {
				'primary-bg': '#23272A',
				'secondary-bg': '#23272A',
				'tertiary-bg': '#36393F',
				'quartiary-bg': '#595e68',
				'primary-color': '#2A5EE8',
				'primary-border': '#DCDDE1',
				'primary-text': '#7289DA',
				'secondary-text': 'white',
			},
			borderWidth: {
				3: '3px',
			},
			padding: {
				'12.5%': '12.5%',
				'18.75%': '18.75%',
			},
			margin: {
				'12.5%': '12.5%',
				'18.75%': '18.75%',
			},
			borderWidth: {
				1: '1px',
			},
			height: {
				fit: 'fit-content',
			},
			width: {
				fit: 'fit-content',
			},
		},
		boxShadow: {
			xs: '0 0 0 1px rgba(0, 0, 0, 0.05)',
			sm: '0 0px 2px 0 rgba(0, 0, 0, 0.05)',
			default:
				'0 0px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px 0 rgba(0, 0, 0, 0.06)',
			md:
				'0 0px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06)',
			lg:
				'0 0px 15px 5px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05)',
			xl:
				'0 0px 25px -5px rgba(0, 0, 0, 0.1), 0 10px 10px -5px rgba(0, 0, 0, 0.04)',
			'2xl': '0 0px 50px -12px rgba(0, 0, 0, 0.25)',
			'3xl': '0 0px 60px -15px rgba(0, 0, 0, 0.3)',
			inner: 'inset 0 0px 4px 0 rgba(0, 0, 0, 0.06)',
			outline: '0 0 0 3px rgba(66, 153, 225, 0.5)',
			focus: '0 0 0 3px rgba(66, 153, 225, 0.5)',
			none: 'none',
		},
	},
	plugins: [require('@tailwindcss/forms'), require('tailwind-scrollbar')],
};
