import React, { useReducer } from 'react';
import { createRef, useEffect, useState } from 'react';
import { AlertCircle, CheckCircle, Search, Star } from 'react-feather';
import Header2 from '../components/Header2';
import TemplateOne from '../components/templates/Template1';
import TemplateTwo from '../components/templates/Template2';
import TemplateThree from '../components/templates/Template3';
import Link from 'next/link';
import faker from 'faker';
import Unsplash from 'react-unsplash-wrapper';
import EditorContext, {
	Product,
	Scene,
	SceneManager,
} from '../react/editor-context';
import DiscordStatus from '../react/discord-status';
import PreviewStatusBar from '../components/preview/PreviewStatusBar';
import ScenePane from '../components/preview/ScenePane';
import {
	isSceneType,
	SceneManagerReducer,
} from '../react/scenemanager-reducer';
import { nanoid } from 'nanoid';
import EditorInputType from '../react/editor-input';

// const PRODUCT_NAME = 'Free Template #1';
const PRODUCT = new Product({
	id: nanoid(12),
	name: faker.commerce.productName(),
	createdAt: new Date(),
	updatedAt: new Date(),
	author: 'StatusCord Official',
	price: 10,
	variants: [
		{
			id: nanoid(12),
			name: 'Variant #1',
			previewImage: faker.image.image(),
			sceneObject: <img src={faker.image.image()} className="rounded-lg" />,
			editorInputs: [
				{
					key: 'discord-name',
					label: `Discord username`,
					type: EditorInputType.TEXT_INPUT,
				},
				{
					key: 'description-tag',
					label: 'Custom description tag',
					type: EditorInputType.TEXT_INPUT,
				},
			],
		},
	],
});

const ProductPreviewApp = ({ id }: { id: string }) => {
	const headerRef = createRef<HTMLDivElement>();
	const containerRef = createRef<HTMLDivElement>();
	const sceneContainerRef = createRef<HTMLDivElement>();
	//FIX OFFSET HEIGHT SCROLLBAR ISSUE

	const [previewStatus, setPreviewStatus] = useState<DiscordStatus | null>(
		DiscordStatus.ONLINE
	);
	// const [activeScene, setActiveScene] = useState<string | null>(null);

	const [sceneManager, dispatchSceneManager] = useReducer(SceneManagerReducer, {
		activeScene: null,
		scenes: [],
	});

	// const sceneManager = new SceneManager({ scenes: null });
	// sceneManager.addScene(
	// 	new Scene({
	// 		display: 'Scene 1',
	// 		previewImage: faker.image.image(),
	// 		sceneObject: (
	// 			<img src="https://i.imgur.com/vWcjNCX.png" className="rounded-lg" />
	// 		),
	// 		editorInputs: [],
	// 	})
	// );
	// console.log('scene id', sceneManager.getScenes()[0].id);
	// console.log('scene id 2', sceneManager.getActiveScene());
	// sceneManager.setActiveScene(sceneManager.getScenes()[0].id);

	useEffect(() => {
		console.log('RERENDER');
		containerRef.current!.style.height = `calc(100vh - ${
			headerRef.current!.getBoundingClientRect().height
		}px)`;

		sceneContainerRef.current &&
			(sceneContainerRef.current.oncontextmenu = function () {
				return false;
			});
	}, []);

	useEffect(() => {
		PRODUCT.variants.forEach((variant) => {
			dispatchSceneManager({
				type: 'ADD',
				payload: new Scene({
					id: variant.id,
					display: variant.name,
					previewImage: variant.previewImage,
					sceneObject: variant.sceneObject,
				}),
			});
		});
		// dispatchSceneManager({
		// 	type: 'ADD',
		// 	payload: new Scene({
		// 		display: 'Scene 1',
		// 		previewImage: faker.image.image(),
		// 		sceneObject: (
		// 			// <img src="https://i.imgur.com/vWcjNCX.png" className="rounded-lg" />
		// 			<img src={faker.image.image()} className="rounded-lg" />
		// 		),
		// 		editorInputs: [],
		// 	}),
		// });
		// dispatchSceneManager({
		// 	type: 'ADD',
		// 	payload: new Scene({
		// 		display: 'Scene 1',
		// 		previewImage: faker.image.image(),
		// 		sceneObject: (
		// 			// <img src="https://i.imgur.com/vWcjNCX.png" className="rounded-lg" />
		// 			<img src={faker.image.image()} className="rounded-lg" />
		// 		),
		// 		editorInputs: [],
		// 	}),
		// });
	}, []);
	// useEffect(() => {
	// 	console.log(
	// 		'RERENDER scene manager',
	// 		sceneManager.activeScene,
	// 		sceneManager.getActiveScene()
	// 	);
	// }, [activeScene]);

	// console.log('active scene', sceneManager.getActiveScene());

	return (
		<EditorContext.Provider
			value={{
				product: PRODUCT,
				previewStatus: previewStatus,
				setPreviewStatus: setPreviewStatus,
				sceneManager: sceneManager,
				dispatchSceneManager: dispatchSceneManager,
			}}
		>
			<div className="h-full">
				<Header2 ref={headerRef} />
				{/* <div className="w-full py-3 bg-secondary-bg text-white px-12.5%">
				<p>
					Are you interested in getting access to premium banners, 20% off
					banner packs, & more?{' '}
					<span className="">
						Check out our <span className="underline">premium plans</span>.
					</span>
				</p>
			</div> */}
				<div className="flex flex-col h-full" ref={containerRef}>
					<div className="flex-1 h-full px-12.5% py-12 grid grid-cols-6 gap-12">
						<div className="col-span-4 flex flex-col items-center justify-center">
							<div className="w-4/5 shadow-lg">
								{/* <p>{sceneManager.activeScene || 'None'}</p> */}
								<ScenePane
									scene={
										isSceneType(sceneManager.activeScene)
											? sceneManager.activeScene
											: null
									}
									discordStatus={previewStatus}
								/>
							</div>
							<PreviewStatusBar className="mt-6" />
						</div>
						<div className="col-span-2 bg-secondary-bg p-6 rounded-md flex flex-col">
							<p>
								{(PRODUCT.variants &&
									PRODUCT.variants[0] &&
									PRODUCT.variants[0].name) ||
									'None'}
							</p>
							<h3 className="mt-1">Customize {PRODUCT.name}</h3>
							{sceneManager.activeScene &&
							PRODUCT.variants.find(
								(variant) => variant.id == sceneManager.activeScene.id
							)
								? PRODUCT.variants
										.find(
											(variant) => variant.id == sceneManager.activeScene.id
										)!
										.editorInputs.map((editorInput) => {
											return (
												<div className="flex flex-col space-y-1 mt-8">
													<label
														htmlFor="discord-username"
														className="text-primary-text font-medium"
													>
														{editorInput.key}
													</label>
													<input
														id="discord-username"
														className="border-quartiary-bg border-2 bg-transparent p-2 rounded-md text-base placeholder-secondary-text"
														placeholder="Enter discord username"
													/>
												</div>
											);
										})
								: null}
							{/* <div className="flex flex-col space-y-1 mt-8">
								<label
									htmlFor="discord-username"
									className="text-primary-text font-medium"
								>
									Discord username
								</label>
								<input
									id="discord-username"
									className="border-quartiary-bg border-2 bg-transparent p-2 rounded-md text-base placeholder-secondary-text"
									placeholder="Enter discord username"
								/>
							</div>
							<div className="flex flex-col space-y-1 mt-6">
								<label
									htmlFor="description-tag"
									className="text-primary-text font-medium"
								>
									Description tag
								</label>
								<input
									id="description-tag"
									className="border-quartiary-bg border-2 bg-transparent p-2 rounded-md text-base placeholder-secondary-text"
									placeholder="Add a description tag"
								/>
							</div> */}
							<div className="flex items-center justify-between mt-6">
								<p className="font-medium text-primary-text">
									Show profile badges?
								</p>
								<div>
									<input
										id="show-badges"
										type="checkbox"
										className="border-quartiary-bg border-2 bg-transparent p-2 rounded-md text-base placeholder-secondary-text"
										placeholder="Add a description tag"
									/>
									<label
										htmlFor="show-badges"
										className="text-primary-text ml-2"
									>
										Yes, include them
									</label>
								</div>
							</div>
							<div className="flex flex-col flex-1 justify-end items-center">
								<button className="primary w-full mb-3">
									Purchase custom banner - $10.99
								</button>
								<Link href="/">
									<a className="transparent underline">Save for later</a>
								</Link>
							</div>
						</div>
					</div>
					<div className="flex-none bg-secondary-bg py-6 overflow-hidden">
						<div className="px-12.5%">
							<h5 className="select-none">
								Pack Variants
								{/* <span className="text-sm ml-2 font-normal text-secondary-text">
								25 total
							</span> */}
							</h5>
						</div>
						<div className="mt-3 pl-12.5% overflow-hidden">
							<div className="flex items-center space-x-6 overflow-x-scroll scrollbar scrollbar-track-secondary-bg scrollbar-thump-primary-color">
								{/* {Array.from({ length: 10 }, (k, i) => {
									return (
										<div className="select-none pointer-events-none">
											<img
												className="rounded-lg w-full"
												src={faker.image.image()}
											/>
										</div>
									);
								})} */}
								{sceneManager.scenes.map((scene) => {
									var isActiveScene =
										isSceneType(sceneManager.activeScene) &&
										sceneManager.activeScene.id == scene.id;
									return (
										// <div className="select-none pointer-events-none relative bg-secondary-bg pb-2/3">
										// 	<img
										// 		className="absolute top-0 w-full h-48 object-cover"
										// 		src={scene.previewImage as string}
										// 	/>
										// </div>
										<div
											className={'select-none cursor-pointer'}
											style={
												isActiveScene
													? {
															boxShadow: '0 0 0 1px black',
															// outlineStyle: 'solid',
															// outlineColor: 'white',
															// outlineWidth: '4px',
															// // outlineOffset: '2px',
															maxWidth: '10%',
													  }
													: { maxWidth: '10%' }
											}
											onClick={() => {
												if (isActiveScene) {
													dispatchSceneManager({
														type: 'CLEAR_ACTIVE',
														payload: null,
													});
													return;
												}
												dispatchSceneManager({
													type: 'SET_ACTIVE',
													payload: scene.id,
												});
												console.log(
													'active scene hehe',
													sceneManager.activeScene
												);
											}}
										>
											<img
												className="rounded-lg w-full max-h-32"
												src={scene.previewImage as string}
											/>
											{/* <img
												className="rounded-lg w-full"
												src={faker.image.image()}
											/> */}
										</div>
									);
								})}
							</div>
						</div>
					</div>
				</div>
			</div>
		</EditorContext.Provider>
	);
};

export { ProductPreviewApp };
