import { FontawesomeObject } from '@fortawesome/fontawesome-svg-core';
import { FontAwesomeIconProps } from '@fortawesome/react-fontawesome';
import { CSSProperties } from 'react';
import { ReactNode, Ref } from 'react';

const Button = ({
	children,
	compact,
	className,
	transparent,
	style,
	outline,
	noPadding,
	icon,
}: {
	children?: ReactNode;
	icon?: JSX.Element;
	compact?: boolean;
	transparent?: boolean;
	outline?: boolean;
	className?: string;
	style?: CSSProperties;
	noPadding?: boolean;
}) => {
	var bgColor = !transparent ? 'bg-primary-color' : 'bg-none';
	outline && (bgColor = 'bg-none');

	return (
		<a
			style={style || {}}
			className={`ui-button h-fit rounded-md ${bgColor} ${
				outline && 'border-primary-color border-1'
			} text-${
				outline ? 'primary-color' : 'primary-text'
			} flex items-center gap-2 cursor-pointer ${
				!noPadding && (compact ? 'py-2 px-3' : 'py-3 px-4')
			} ${className || ''}`}
		>
			{icon}
			{children}
		</a>
	);
};

export { Button };
