import React from 'react';
import { createRef, useEffect } from 'react';
import { Check } from 'react-feather';
// import { AlertCircle, CheckCircle } from 'react-feather';
import Header2 from '../components/Header2';
import { default as tailwind } from '../tailwind.config.js';

const PRICING_INFO: {
	name: string;
	description: string;
	price: number;
	features: string[];
}[] = [
	{
		name: 'Free',
		description: 'Free for all, great features.',
		price: 0,
		features: [
			'Access to Free Templates',
			'Up to 1 Active Banner',
			'24/7 Banner Uptime',
		],
	},
	{
		name: 'Premium',
		description: 'Free for all, great features.',
		price: 4.99,
		features: [
			'Access to Free Templates',
			'Access to Premium Templates',
			'Get 25% off Template Packs',
			'Up to 3 Active Banners',
			'Priority Support',
			'24/7 Banner Uptime',
		],
	},
	{
		name: 'Advanced',
		description: 'Free for all, great features.',
		price: 11.99,
		features: [
			'Access to Free Templates',
			'Access to Premium Templates',
			'Get 45% off Template Packs',
			'Unlimited Active Banners',
			'Priority Support',
			'24/7 Banner Uptime',
		],
	},
];

const PricingApp = () => {
	const headerRef = createRef<HTMLDivElement>();
	const containerRef = createRef<HTMLDivElement>();

	//FIX OFFSET HEIGHT SCROLLBAR ISSUE
	useEffect(() => {
		containerRef.current!.style.height = `calc(100vh - ${
			headerRef.current!.getBoundingClientRect().height
		}px)`;
	}, []);

	return (
		<div className="h-full">
			<Header2 ref={headerRef} />
			<div
				className="flex flex-col py-12 items-center bg-primary-bg"
				ref={containerRef}
			>
				{/* <div className="h-1/3 w-4/5 flex flex-col items-center">
					<img
						src="https://i.imgur.com/vWcjNCX.png"
						className="rounded-lg w-1/2"
					/>
				</div> */}
				<h1>Pricing</h1>
				<h3 className="text-secondary-text font-normal mt-3">
					Get access to premium-only templates, discounts on template packs, &
					more with our amazing monthly plans.
				</h3>
				{/* <div className="w-1/2 mt-12">
					<img src="https://i.imgur.com/vWcjNCX.png" className="rounded-lg" />
				</div> */}
				{/* <div className="mt-8 space-x-4">
					<button className="primary">Create my banner</button>
					<button className="secondary">View templates</button>
				</div> */}
				<div
					className={`grid grid-cols-${PRICING_INFO.length} gap-6 w-full px-18.75% mt-12`}
				>
					{PRICING_INFO.map((info) => {
						return (
							<div className="w-full bg-secondary-bg flex flex-col items-center p-8 rounded-md">
								<h2 className="">{info.name}</h2>
								<p className="mt-2">{info.description}</p>
								{/* <h3 className="my-4">
							$0<span className="font-normal">/mo</span>
						</h3> */}
								<div className="my-8 text-center">
									<h2>${info.price}</h2>
									<p className="mt-1">Per month</p>
								</div>
								<div className="w-full text-primary-text text-center">
									<h5>Features</h5>
									<ul className="mt-2 space-y-2">
										<li className="flex items-center justify-center space-x-2 w-full">
											<Check
												size={24}
												color={tailwind.theme.extend.colors['primary-color']}
											/>{' '}
											<span>Access to Free Templates</span>
										</li>
										<li>
											<Check /> Up to 1 Banner Draft
										</li>
										<li>
											<Check /> 24/7 Banner Uptime
										</li>
									</ul>
								</div>
							</div>
						);
					})}
				</div>
			</div>
		</div>
	);
};

export { PricingApp };
