import { FontawesomeObject } from '@fortawesome/fontawesome-svg-core';
import { FontAwesomeIconProps } from '@fortawesome/react-fontawesome';
import { CSSProperties } from 'react';
import { ReactNode, Ref } from 'react';

const Button = ({
	children,
	compact,
	className,
	transparent,
	style,
	noPadding,
	icon,
}: {
	children?: ReactNode;
	icon?: JSX.Element;
	compact?: boolean;
	transparent?: boolean;
	className?: string;
	style?: CSSProperties;
	noPadding?: boolean;
}) => {
	return (
		<div
			style={style || {}}
			className={`${
				!transparent && 'bg-secondary-bg'
			} w-fit h-fit bg-primary-color rounded-full text-primary-text ${
				!noPadding && (compact ? 'py-2 px-3' : 'py-3 px-4')
			} ${className || ''}`}
		>
			{icon}
			{children}
		</div>
	);
};

export { Button };
