import {
	faBoxOpen,
	faExternalLinkAlt,
	faPaperPlane,
} from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import Link from 'next/link';
import React, { ReactNode } from 'react';
import { createRef, useEffect } from 'react';
// import { AlertCircle, CheckCircle } from 'react-feather';
import Header from '../components/Header';
import { Panel } from '../components/Panel';
import Sidebar from '../components/Sidebar';
import { default as faker } from 'faker';
import { Button } from '../components/Button';
// import TemplateOne from '../components/templates/Template1';
// import TemplateTwo from '../components/templates/Template2';
// import TemplateThree from '../components/templates/Template3';

const CHAT_MESSAGES: {
	author: { name: string; icon: string };
	content: string;
}[] = [
	{
		author: {
			name: faker.name.findName(),
			// icon: 'https://cdn.discordapp.com/avatars/798294419904004166/00030c0c0dcd5da64513327db5e219ed.webp?size=256',
			icon: faker.image.imageUrl(),
		},
		content: faker.lorem.text().trim().substring(0, 180),
	},
	{
		author: {
			name: faker.name.findName(),
			// icon: 'https://cdn.discordapp.com/avatars/798294419904004166/00030c0c0dcd5da64513327db5e219ed.webp?size=256',
			icon: faker.image.imageUrl(),
		},
		content: faker.lorem.text().trim().substring(0, 180),
	},
	{
		author: {
			name: faker.name.findName(),
			// icon: 'https://cdn.discordapp.com/avatars/798294419904004166/00030c0c0dcd5da64513327db5e219ed.webp?size=256',
			icon: faker.image.imageUrl(),
		},
		content: faker.lorem.text().trim().substring(0, 180),
	},
	{
		author: {
			name: faker.name.findName(),
			// icon: 'https://cdn.discordapp.com/avatars/798294419904004166/00030c0c0dcd5da64513327db5e219ed.webp?size=256',
			icon: faker.image.imageUrl(),
		},
		content: faker.lorem.text().trim().substring(0, 180),
	},
	{
		author: {
			name: faker.name.findName(),
			// icon: 'https://cdn.discordapp.com/avatars/798294419904004166/00030c0c0dcd5da64513327db5e219ed.webp?size=256',
			icon: faker.image.imageUrl(),
		},
		content: faker.lorem.text().trim().substring(0, 180),
	},
];

const HomeApp = ({ children }: { children?: ReactNode }) => {
	const headerRef = createRef<HTMLDivElement>();
	const containerRef = createRef<HTMLDivElement>();

	return (
		<div className="h-full">
			<Header ref={headerRef} />
			<div ref={containerRef} className="px-12.5% pt-12 grid grid-cols-7 gap-6">
				<div className="col-span-5">
					<Panel
						style={{ maxHeight: '650px' }}
						className="overflow-hidden"
						noPadding={true}
					>
						<div className="flex items-center justify-between px-4 pt-4">
							<h4>Chat Room</h4>
							<a href="/" target="_blank">
								<FontAwesomeIcon icon={faExternalLinkAlt} className="mr-2" />
								Open in new window
							</a>
						</div>
						<hr className="mt-3" />
						<div className="grid grid-cols-4 gap-6">
							<div className="col-span-3 overflow-hidden relative">
								<ul className="space-y-6 overflow-y-scroll pt-4 px-4">
									{CHAT_MESSAGES.map((msg) => {
										return (
											<li className="w-full overflow-hidden truncate">
												<div className="flex space-x-2">
													{/* <div className="min-w-6 min-h-6 w-6 h-6 bg-tertiary-bg rounded-full"></div> */}
													<img
														src={msg.author.icon}
														className="w-6 h-6 rounded-full"
													/>
													<div>
														<h6>{msg.author.name}</h6>
														<div className="bg-tertiary-bg p-3 mt-2 rounded-sm overflow-hidden truncate">
															<p className="text-primary-text text-sm truncate">
																{msg.content}
															</p>
														</div>
													</div>
												</div>
											</li>
										);
									})}
								</ul>
								<div className="absolute left-0 right-0 bottom-0 bg-tertiary-bg w-full p-3 flex items-center justify-between">
									<input
										placeholder="Type a chat message..."
										className="bg-transparent w-full"
									/>
									{/* <button className="">Send</button> */}
									<Button
										compact={true}
										icon={<FontAwesomeIcon icon={faPaperPlane} />}
									>
										Send
									</Button>
								</div>
							</div>
							<div>
								<ul className="space-y-2 pt-4">
									{Array(4)
										.fill(null)
										.map(() => {
											return {
												name: faker.name.findName(),
												icon: faker.image.imageUrl(),
											};
										})
										.map((user) => {
											return (
												<li className="flex items-center space-y-2">
													<img
														src={user.icon}
														className="w-6 h-6 rounded-full"
													/>
													{user.name}
												</li>
											);
										})}
								</ul>
							</div>
						</div>
						<div></div>
					</Panel>
				</div>
				<div className="col-span-2">
					<Panel>
						<h4>Recent Posts</h4>
					</Panel>
				</div>
			</div>
		</div>
	);
};

export default HomeApp;
