import { SetStateAction } from 'react';
import { Dispatch } from 'react';
import { createContext } from 'react';
// import { FilteredResultsAction } from '../reducers/FilteredResultsReducer';
import DiscordStatus from './discord-status';
import { EditorInputType } from './editor-input';
// import { CartItem, CartItems, CartReducer } from "../reducers/CartReducer";
import { nanoid } from 'nanoid';
import { stringify } from 'querystring';
import { v4 as uuidv4 } from 'uuid';

interface SceneProps {
	// id: string;
	display: string;
	previewImage: String | Blob;
	sceneObject: JSX.Element | null;
	editorInputs: {
		key: { internal: string; display: string };
		type: EditorInputType;
	}[];
}

class Scene {
	id: string;
	display: string;
	previewImage: String | Blob;
	sceneObject: JSX.Element | null;
	editorInputs: {
		key: { internal: string; display: string };
		type: EditorInputType;
	}[];

	constructor({
		display,
		previewImage,
		sceneObject,
		editorInputs,
	}: SceneProps) {
		// this.id = nanoid(12);
		this.id = uuidv4();
		this.display = display;
		this.previewImage = previewImage;
		this.sceneObject = sceneObject;
		this.editorInputs = editorInputs;
	}

	isActiveScene(sceneManager: SceneManager) {
		this.id == sceneManager.activeScene;
	}
}

class SceneManager {
	scenes: Scene[];
	activeScene: string | null;

	constructor({ scenes }: { scenes: Scene[] | null }) {
		this.scenes = scenes || [];
		this.activeScene = (scenes && scenes[0].id) || null;
	}

	getActiveScene(): Scene | null {
		console.log(
			'active scene find',
			this.activeScene,
			this.scenes,
			this.scenes.find((scene) => scene.id == this.activeScene)
		);
		return this.activeScene
			? this.scenes.find((scene) => scene.id == this.activeScene) || null
			: null;
	}

	setActiveScene(
		id: string,
		sceneDispatch: React.Dispatch<React.SetStateAction<string | null>>
	) {
		console.log('set active scene', this.activeScene, id);
		this.activeScene = id;
		sceneDispatch(this.activeScene);
		console.log(
			'set active scene',
			this.getActiveScene(),
			this.activeScene,
			id
		);
	}

	getScenes() {
		return this.scenes;
	}

	addScene(scene: Scene) {
		this.scenes.push(scene);
	}
}

interface IContext {
	productName: string;
	previewStatus: DiscordStatus | null;
	setPreviewStatus: Dispatch<SetStateAction<DiscordStatus | null>>;
	sceneManager: SceneManager;
}

const contextProps: IContext = {
	productName: '',
	previewStatus: null,
	setPreviewStatus: (null as unknown) as Dispatch<
		SetStateAction<DiscordStatus | null>
	>,
	sceneManager: new SceneManager({ scenes: null }),
};

const EditorContext = createContext<IContext>(contextProps);

export default EditorContext;
export { Scene, SceneManager };
